mapper.select:
SELECT AMORTIZACION_CREDITOS.SEC, AMORTIZACION_CREDITOS.SEC_CREDITO, AMORTIZACION_CREDITOS.PERIODO, AMORTIZACION_CREDITOS.CUOTA, AMORTIZACION_CREDITOS.INTERES, AMORTIZACION_CREDITOS.SALDO, AMORTIZACION_CREDITOS.FECHA_CREACION, AMORTIZACION_CREDITOS.FECHA_HORA_ACTUALIZACION, CREDITOS.SEC_BANCO, BANCOS.codigo COD_BANCO, BANCOS.nombre NOMBRE_BANCO, CREDITOS.NUMERO_PRESTAMO, CREDITOS.FECHA_VENCIMIENTO, CREDITOS.FECHA, CREDITOS.CAPITAL CAPITAL_CREDITO, CREDITOS.SALDO SALDO_CREDITO, AMORTIZACION_CREDITOS.CAPITAL FROM AMORTIZACION_CREDITOS left join CREDITOS on CREDITOS.sec= AMORTIZACION_CREDITOS.SEC_CREDITO left join BANCOS on BANCOS.SEC = CREDITOS.SEC_BANCO
AND AMORTIZACION_CREDITOS.SEC_CREDITO = #{credito.secuencia,jdbcType=NUMERIC}
AND CREDITOS.SEC_BANCO = #{credito.entidad.sec,jdbcType=NUMERIC}
