mapper.select:
SELECT PROVEEDORES.SEC AS SEC_PROVEEDOR , PROVEEDORES.FECHA_PAGO , PROVEEDORES.FECHA_HORA_ACTUALIZACION , PROVEEDORES.SEC_USUARIO , PROVEEDORES.PROVEEDOR , USUARIOS.CUENTA , DETALLE_PROVEEDOR . SEC AS SEC , DETALLE_PROVEEDOR . SEC_PROVEEDOR , DETALLE_PROVEEDOR . VENCIMIENTO , DETALLE_PROVEEDOR . REFERENCIA , DETALLE_PROVEEDOR . NRO_FACTURA, DETALLE_PROVEEDOR .VALOR_1, DETALLE_PROVEEDOR .VALOR_2, DETALLE_PROVEEDOR .VALOR_3, DETALLE_PROVEEDOR .VALOR_4, DETALLE_PROVEEDOR .FECHA_CREACION, DETALLE_PROVEEDOR .FECHA_ACTUALIZACION FROM DETALLE_PROVEEDOR LEFT JOIN PROVEEDORES ON PROVEEDORES .SEC = DETALLE_PROVEEDOR . SEC_PROVEEDOR LEFT JOIN USUARIOS ON USUARIOS.SEC = PROVEEDORES.SEC_USUARIO
AND PROVEEDORES.SEC = #{SEC_PROVEEDOR,jdbcType=NUMERIC};
AND PROVEEDORES.PROVEEDOR = #{proveedor,jdbcType=VARCHAR};
