mapper.update:
UPDATE DISPONIBLES 
   SET SEC_USUARIO = #{usuario.secuencia,jdbcType=NUMERIC},
       SEC_BANCO = #{banco.sec,jdbcType=NUMERIC},
       SEC_CUENTA  = #{cuenta.secuencia,jdbcType=NUMERIC},
       FECHA_HORA_ACTUALIZACION =  #{fechaHoraActualizacion,jdbcType=DATE}
 WHERE SEC = #{secuencia,jdbcType=NUMERIC};

mapper.select:
SELECT DISPONIBLES.SEC, CUENTAS.SEC SEC_CUENTA, CUENTAS.SEC_BANCO, CUENTAS.RESPONSABLE, CUENTAS.NUMERO, CUENTAS.ESTADO, CUENTAS.TIPO, BANCOS.SEC SEC_BANCO, BANCOS.CODIGO, BANCOS.NOMBRE, BANCOS.ESTADO, DISPONIBLES.SEC_USUARIO, USUARIOS.SEC_PERSONA, DISPONIBLES.FECHA_CREACION, DISPONIBLES.FECHA_HORA_ACTUALIZACION
FROM DISPONIBLES JOIN BANCOS ON BANCOS.SEC = DISPONIBLES .SEC_BANCO JOIN CUENTAS ON CUENTAS.SEC_BANCO = DISPONIBLES.SEC_BANCO JOIN USUARIOS ON USUARIOS.SEC = DISPONIBLES.SEC_USUARIO
AND DISPONIBLES .SEC_BANCO = #{banco.sec,jdbcType=NUMERIC};
